import {useState} from "react"

export default function SearchField(){
    const myAPIkey = "UiZGWrYMCET0AZBfmHq49TPkz6vNFFSb";
    const data = `http://api.giphy.com/v1/gifs/search?q=${userInput}&api_key=${myAPIkey}`
    console.log(data);
    const [userInput, setUserInput] = useState("")

    const handleSubmit = (event) =>{
        event.preventDefault();
        alert("The user entered: " + userInput);
    }
    return(
        <form onSubmit={handleSubmit}className="gif--form">
            <label>Search for gif: </label>
            <input 
                type="text"
                value={userInput}
                onChange={(e => setUserInput(e.target.value))}
            />
            <input type="submit"/>
        </form>
    )
}
