import React from "react"
import {useState, useEffect} from "react" 
import searchFieldCss from "../searchField.css"
import GifCard from "./GifCard"
import axios from 'axios';

export default function SearchField(){
    const myAPIkey = "UiZGWrYMCET0AZBfmHq49TPkz6vNFFSb";

    const [userInput, setUserInput] = useState("");
    const [apiUrl, setApiUrl] = useState("");
    const [gifArray, setGifArray] = useState([]);


    const handleSubmit = (event) =>{
        event.preventDefault();
        console.log(userInput);

        setApiUrl(`http://api.giphy.com/v1/gifs/search?q=${userInput}&api_key=${myAPIkey}`)

        fetch(apiUrl)
            .then(res => res.json())
            .then(dataJson =>{
                setGifArray(dataJson.data);
                gifArray.map(gif =>{
                    <GifCard 
                        url = {gif.url}
                    />
                })
            });
            //console.log(gifArray);
    }
    return(
        <main>
        <form className="gif--form">
            <label className="label">Search for gif: </label>
            <input 
                className="input-field"
                type="text"
                value={userInput}
                onChange={(e => setUserInput(e.target.value))}
            />
            <input type="submit" className="submit-btn" value="Submit" onClick={handleSubmit}/>
        </form>

        </main>
    )
}
